mapper.update:
UPDATE Post 
SET med_id = #{med_id}, title = #{title}, content = #{content} 
WHERE post_id = #{post_id}

mapper.select:
 
		SELECT post_id, user_id, med_id, post_date, title, content, ingestion_start, ingestion_end
		FROM Post
		WHERE post_id = #{post_id}

SELECT post_id, user_id, med_id, post_date, title, content, ingestion_start, ingestion_end
FROM Post
WHERE user_id = #{user_id}
